result:
.scratchpad {
  padding: 0 100px;
}

.shape {
  position: relative;
  transform-style: preserve-3d;
  transition: transform 1s;

  &__face {
    position: absolute;
    font-size: 10px;
    font-weight: bold;
    color: white;
    text-align: center;
  }
}

$triangle-size: 100px;
.shape--triangle {
  transform: translateZ(-50px) rotateX(0deg) rotateY(0deg);
  width: $triangle-size;
  height: $triangle-size;

  .shape__face {
    &--front {
      width: 0;
      height: 0;
      border-left: ($triangle-size / 2) solid transparent;
      border-right: ($triangle-size / 2) solid transparent;
      border-bottom: $triangle-size solid red;
      line-height: $triangle-size;
    }

    &--left {
      width: 0;
      height: 0;
      border-left: ($triangle-size / 2) solid transparent;
      border-right: ($triangle-size / 2) solid transparent;
      border-bottom: $triangle-size solid red;
      line-height: $triangle-size;
    }

    &--right {
      width: 0;
      height: 0;
      border-left: ($triangle-size / 2) solid transparent;
      border-right: ($triangle-size / 2) solid transparent;
      border-bottom: $triangle-size solid red;
      line-height: $triangle-size;
    }

    &--bottom {
      width: 0;
      height: 0;
      border-left: ($triangle-size / 2) solid transparent;
      border-right: ($triangle-size / 2) solid transparent;
      border-top: $triangle-size solid red;
      line-height: $triangle-size;
    }

    &--left,
    &--right {
      left: 0px;
    }

    &--bottom {
      top: 0px;
    }

    &--front  { transform: rotateY(  0deg) translateZ(($triangle-size / 2)); }
    &--left   { transform: rotateY(-90deg) translateZ(($triangle-size / 2)); }
    &--right  { transform: rotateY( 90deg) translateZ(($triangle-size / 2)); }
    &--bottom { transform: rotateX(-90deg) translateZ(($triangle-size / 2)); }

    &--front  { border-bottom-color: hsla(  0, 100%, 50%, 0.99); }
    &--left   { border-bottom-color: hsla(180, 100%, 50%, 0.99); }
    &--right  { border-bottom-color: hsla( 60, 100%, 50%, 0.99); }
    &--bottom { border-top-color: hsla(300, 100%, 50%, 0.99); }
  }
}


$cube-size: 100px;
.shape--cube {
  width: $cube-size;
  height: $cube-size;
  transform: translateZ(-50px) rotateX(0deg) rotateY(0deg);

  .shape__face {
    &--front,
    &--back,
    &--left,
    &--right,
    &--top,
    &--bottom {
      width: $cube-size;
      height: $cube-size;
      line-height: $cube-size;
    }

    &--left,
    &--right {
      left: 0px;
    }

    &--top,
    &--bottom {
      top: 0px;
    }

    &--front  { transform: rotateY(  0deg) translateZ(($cube-size / 2)); }
    &--back   { transform: rotateY(180deg) translateZ(($cube-size / 2)); }

    &--left   { transform: rotateY(-90deg) translateZ(($cube-size / 2)); }
    &--right  { transform: rotateY( 90deg) translateZ(($cube-size / 2)); }

    &--top    { transform: rotateX( 90deg) translateZ(($cube-size / 2)); }
    &--bottom { transform: rotateX(-90deg) translateZ(($cube-size / 2)); }

    &--front  { background: hsla(  0, 100%, 50%, 0.99); }
    &--right  { background: hsla( 60, 100%, 50%, 0.99); }
    &--back   { background: hsla(120, 100%, 50%, 0.99); }
    &--left   { background: hsla(180, 100%, 50%, 0.99); }
    &--top    { background: hsla(240, 100%, 50%, 0.99); }
    &--bottom { background: hsla(300, 100%, 50%, 0.99); }
  }
}
